(ns aoc.y2015.day1)

(defn- navigate [cursor direction]
  (cond
    (= \( direction) (inc cursor)
    (= \) direction) (dec cursor)))

(defn solve [input]
  (reduce navigate 0 input))

(defn solve2 [input]
  (->>
    (reductions navigate 0 input)
    (keep-indexed #(if (not (pos? %2)) %1))
    (next)
    (first)))

