(ns aoc.y2022.day6)

(defn solve
  ([input] (solve input 4))
  ([input size]
   (->> (partition size 1 input)
        (keep-indexed #(if (= size (count (distinct %2))) %1))
        (first)
        (+ size))))

(defn solve2 [input]
  (solve input 14))

(comment
  (solve input)
  (solve2 input))

(def input "")